mapper.select:
select eduId as edu_id,
       title,
       titleLink as title_link,
       company,
       companyLink as company_link,
       tel_no as telNO,
       address,
       content,
       period,
       onoff,
       expense,
       realExpense as real_expense,
       detail,
       man
from edu



            title like #{item}
            or company like #{item}
            or detail like #{item}
            or content like #{item}


            or title like #{item}
            or company like #{item}
            or detail like #{item}
            or content like #{item}


    ;
