mapper.select:
select jobId as edu_id,
       companyName as company_name,
       wantedTitle as wanted_title,
       companyUrl as company_url,
       degree,
       major,
       address,
       workAddress as work_address,
       preference,
       certificate,
       jobContent as job_content,
       sal,
       employmentForm as employment_form,
       endDate as end_date
from job_post



            company_name like #{item}
            or wanted_title like #{item}
            or degree like #{item}
            or major like #{item}
            or certificate like #{item}
            or preference like #{item}
            or jobContent like #{item}


            or company_name like #{item}
            or wanted_title like #{item}
            or degree like #{item}
            or major like #{item}
            or certificate like #{item}
            or preference like #{item}
            or jobContent like #{item}


    ;
